package Demo1;
import static java.lang.Math.*;

public class PowerRoot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double base = 4;
		double exponent = 3;
		double power = pow(base, exponent);
		double root = sqrt(base);
		
		System.out.println(base + " raised to power of " + exponent + " is: " + power);
		System.out.println("Square root of " + base + " is: " + root);

	}

}
